    ctx->tuning_params.validatezisegred_tblock_sizze_14298 = &ctx->cfg->tuning_params[67];
    ctx->tuning_params.validatezisuff_intra_par_1 = &ctx->cfg->tuning_params[68];
    ctx->tuning_params.validatezisuff_outer_par_0 = &ctx->cfg->tuning_params[69];
